sysauto_script.script:
// -------------------------------------------------------------------------------------------------------------------------------
// checkOverdueDates
//
// a scheduled script execution that will run once daily (at 12 am) and automatically label tasks based on their due date's relation with the 
// current date. an overdue task will be marked RED, a nearly overdue task (3 days) will be marked YELLOW
// -------------------------------------------------------------------------------------------------------------------------------
// GET REAL DATE INFORMATION
// 
// create GlideDateTime object to get raw real date; convert strings to integer for later comparison
var gdt = new GlideDateTime();
var rawDate = gdt.getValue();
var realYear = Number(rawDate.substring(0, 4));
var realMonth = Number(rawDate.substring(5, 7));
var realDay = Number(rawDate.substring(8, 10));
gs.info('realDate: ' + realYear + '/' + realMonth + '/' + realDay);
// -------------------------------------------------------------------------------------------------------------------------------	
// QUERY RECORDS FROM STATUSTASKTABLE
//
// get gliderecord representation of our table
var gr = new GlideRecord('x_828313_demandtha_statustasktable');
// get today's time and date
gr.addQuery('due_date','>=', gdt);
gr.addQuery('due_date','<',gs.hoursAgo(-72));
gr.addQuery('status','!=','complete');
gr.query();
// Write a log message for each Cloud Service Task Record
while(gr.next()){
    gs.info("Cloud Service Task due soon record = " + gr.number);
}
// -------------------------------------------------------------------------------------------------------------------------------
// ITERATING THROUGH THE RECORDS
//
while(gr.next())
{
	// -------------------------------------------------------------------------------------------------------------------------------
	// GET AND FORMAT RECORD OBJECT'S DATE
	//
	// get due date of current record object; convert strings to integer for later comparison
	var recordDate = gr.due_date;
	var recordYear = Number(recordDate.substring(0, 4));
	var recordMonth = Number(recordDate.substring(5, 7));
	var recordDay = Number(recordDate.substring(8, 10));
	gs.info('recordDate: ' + recordYear + '/' + recordMonth + '/' + recordDay);

	// --------------------------------------------------------------------------------------------------------------------------
	// VALIDATE THE DATE
	//
	// due date year before current year; mark as OVERDUE
	if (recordYear < realYear){
		//RED
	}
	// referencing the same year; continue to month comparison
	if (recordYear == realYear){
		// due date month before current month; mark as OVERDUE
		if (recordMonth < realMonth){
			//RED
		}
		// referencing the same month; continue to day comparison
		if (recordMonth == realMonth){
			// due date day before or equal to current day; mark as OVERDUE
			if (recordDay <= realDay){
				//RED
			} 
			// due date day 3 or less days away from current day; mark as NEARLY OVERDUE
			if (recordDay - realDay){
				//YELLOW
				gs.info("hitYellow");
			}
		}
	}
}
